sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// get current date
	var rightnow = new GlideDateTime();
//	var dayOfToday = rightnow.getDayOfMonthLocalTime();

	// get request date
	var whenNeeded = new GlideDateTime( current.u_when_needed );

	// check if year of whenNeeded is before today
	if( whenNeeded.getYearLocalTime() <= rightnow.getYearLocalTime()){

		// check if Month of whenNeeded is before today
		if( whenNeeded.getMonthLocalTime() <= rightnow.getMonthLocalTime()){

			// check if day of whenNeeded is before tomorrow
			if( whenNeeded.getDayOfMonthLocalTime() <= rightnow.getDayOfMonthLocalTime()){
				gs.addErrorMessage("whenNeeded field must be set after today. Your request is aborted!");
				current.setAbortAction(true);
			}
		}
	}

})(current, previous);
